using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;
using DogsForUs.Models;

namespace DogsForUs.Controllers
{
    [Route("api/[controller]")]
    public class DogsController : ControllerBase
    {
        private static Dictionary<string, Dog> _dogCollection = new Dictionary<string, Dog>();

        /// <summary>
        /// API Controller which returns the collection of dogs
        /// Usage: HTTP GET ../api/dogs
        /// </summary>
        /// <returns>OK(_dogCollection)</returns>
        [HttpGet]
        public ActionResult GetAllDogs()
        {
            Dictionary<string, Dog> tempCollection = new Dictionary<string, Dog>();
            if (_dogCollection.Count == 0)
            {
                PopulateCollection();
            }
            lock (_dogCollection)
            {
                tempCollection = _dogCollection;
            }
            return Ok(tempCollection);
        }

        /// <summary>
        /// API Controller which adds a new dog to the collection
        /// Usage: HTTP POST ../api/dogs
        /// </summary>
        /// <param name="dog"></param>
        /// <returns>Result()</returns>
        [HttpPost]
        public ActionResult AddDog(Dog dog)
        {
            if (!String.IsNullOrEmpty(dog.Name))
            {
                if (String.IsNullOrEmpty(dog.Description))
                {
                    dog.Description = "No Description.";
                }
                lock (_dogCollection)
                {
                    if (!_dogCollection.ContainsKey(dog.Name))
                    {
                        _dogCollection[dog.Name] = dog;
                        return Ok();
                    }
                }
            }
            return BadRequest();
        }

        /// <summary>
        /// API Controller which edits a dog in the collection
        /// Usage: HTTP PUT ../api/dogs/Original Dog Name - new dog details added in x-www-form-urlencoded 
        /// </summary>
        /// <param name="originalName"></param>
        /// <param name="newDog"></param>
        /// <returns>Result()</returns>
        [HttpPut("{originalName}")]
        public ActionResult EditDog(string originalName, Dog newDog)
        {
            if (!String.IsNullOrEmpty(newDog.Name))
            {
                if (String.IsNullOrEmpty(newDog.Description))
                {
                    newDog.Description = "No Description.";
                }
                lock (_dogCollection)
                {
                    if (_dogCollection.ContainsKey(originalName))
                    {
                        if (originalName.Equals(newDog.Name))
                        {
                            _dogCollection[newDog.Name] = newDog;
                            return Ok();
                        }
                        else if (!_dogCollection.ContainsKey(newDog.Name))
                        {
                            _dogCollection.Remove(originalName);
                            _dogCollection[newDog.Name] = newDog;
                            return Ok();
                        }
                    }
                }
            }
            return BadRequest();
        }

        /// <summary>
        /// API Controller which deletes a dog from the collection
        /// Usage: HTTP DELETE ../api/dogs/DogName
        /// </summary>
        /// <param name="name"></param>
        /// <returns>Result()</returns>
        [HttpDelete("{name}")]
        public ActionResult DeleteDog(string name)
        {
            if (!String.IsNullOrEmpty(name))
            {
                lock (_dogCollection)
                {
                    if (_dogCollection.ContainsKey(name))
                    {
                        _dogCollection.Remove(name);
                        return Ok();
                    }
                }
            }
            return BadRequest();
        }

        /// <summary>
        /// Method to clear the _dogCollection - used for UT
        /// </summary>
        public void Clear()
        {
            if (_dogCollection != null && _dogCollection.Count != 0)
            {
                lock (_dogCollection)
                {
                    _dogCollection.Clear();
                }
            }
        }

